OcrEngine ocr = new OcrEngine();
ocr.Image = ImageStream.FromFile(@"image.tiff");
ocr.Languages.AddLanguage(Language.Load("english"));

IRecognitionBlock rectangleRecognitionBlock = RecognitionBlock.FromRectangle(0, 0, 100, 200);
// mark that rectangleRecognitionBlock.BlockId is rectangle (0, 0, 100, 200)
ocr.AddRecognitionBlock(rectangleRecognitionBlock);
IRecognitionBlock pageRecognitionBlock = RecognitionBlock.FromPageBlock(4);
// mark that pageRecognitionBlock.BlockId is page (4)
ocr.AddRecognitionBlock(pageRecognitionBlock);

if (ocr.Process())
{
}
Console.WriteLine(rectangleRecognitionBlock.Text);
Console.WriteLine(pageRecognitionBlock.Text);

